import{_ as e,c as r,o as t,a}from"./app.a810b278.js";const c=JSON.parse('{"title":"Extending Beeminder","description":"","frontmatter":{},"headers":[{"level":2,"title":"Graphical Automation Tools","slug":"graphical-automation-tools"},{"level":2,"title":"Browser-based Customization","slug":"browser-based-customization"},{"level":3,"title":"User Scripts","slug":"user-scripts"},{"level":3,"title":"User Styles","slug":"user-styles"},{"level":2,"title":"Hardware","slug":"hardware"},{"level":2,"title":"Scripting","slug":"scripting"},{"level":3,"title":"API Wrappers","slug":"api-wrappers"},{"level":3,"title":"Example Projects","slug":"example-projects"}],"relativePath":"extending-beeminder.md"}'),n={name:"extending-beeminder.md"},o=a('<h1 id="extending-beeminder" tabindex="-1">Extending Beeminder <a class="header-anchor" href="#extending-beeminder" aria-hidden="true">#</a></h1><p>See also: <a href="./integrations.html">Integrations</a></p><p>Thanks to @philip for granting permission to duplicate the content in his <a href="https://forum.beeminder.com/t/api-coding-resources-list/2947" target="_blank" rel="noopener noreferrer">API Coding Resources List</a> forum thread!</p><h2 id="graphical-automation-tools" tabindex="-1">Graphical Automation Tools <a class="header-anchor" href="#graphical-automation-tools" aria-hidden="true">#</a></h2><ul><li><a href="https://blog.beeminder.com/beedroid/" target="_blank" rel="noopener noreferrer"><strong>Android and Beeminder and Tasker</strong></a></li><li><a href="https://forum.beeminder.com/t/tasker-recipes/5367" target="_blank" rel="noopener noreferrer"><strong>Tasker recipes?</strong></a> - Forum users sharing how they use Tasker with Beeminder.</li><li><a href="https://forum.beeminder.com/t/using-beeminder-with-android-automate/6401" target="_blank" rel="noopener noreferrer"><strong>Using Beeminder with Android Automate</strong></a> - Short tutorial on using the Beeminder API from an Android Automate flow.</li></ul><h2 id="browser-based-customization" tabindex="-1">Browser-based Customization <a class="header-anchor" href="#browser-based-customization" aria-hidden="true">#</a></h2><h3 id="user-scripts" tabindex="-1">User Scripts <a class="header-anchor" href="#user-scripts" aria-hidden="true">#</a></h3><ul><li><a href="https://openuserjs.org/scripts/narthur/Beeminder_Power_Breaks" target="_blank" rel="noopener noreferrer"><strong>Beeminder Power Breaks</strong></a> - User script that adds power features to Beeminder breaks page. Made by @narthur.</li><li><a href="https://openuserjs.org/scripts/narthur/Beeminder_Filters" target="_blank" rel="noopener noreferrer"><strong>Beeminder Filters</strong></a> - User script that adds filter buttons to Beeminder dashboard. Made by @narthur.</li><li><a href="https://openuserjs.org/scripts/narthur/Beeminder_Open_Tabs" target="_blank" rel="noopener noreferrer"><strong>Beeminder Open Tabs</strong></a> - User script that adds links for opening all goals in new tabs from dashboard. Made by @narthur.</li><li><a href="https://greasyfork.org/en" target="_blank" rel="noopener noreferrer"><strong>Greasy Fork</strong></a> - &quot;Welcome to Greasy Fork, a site for user scripts.&quot;</li><li><a href="https://openuserjs.org/" target="_blank" rel="noopener noreferrer"><strong>OpenUserJS</strong></a> - A website for discovering and hosting user scripts.</li></ul><h3 id="user-styles" tabindex="-1">User Styles <a class="header-anchor" href="#user-styles" aria-hidden="true">#</a></h3><ul><li><a href="https://userstyles.org/styles/174385/beeminder-dashboard-dense?utm_campaign=stylish_stylepage" target="_blank" rel="noopener noreferrer"><strong>Beeminder Dashboard Dense</strong></a> - User style that makes the dashbord much denser. Made by @narthur.</li><li><a href="https://freestyler.ws/" target="_blank" rel="noopener noreferrer"><strong>FreeStyler.WS</strong></a> - &quot;FreeStyler has everything you might need to style up websites you visit according to your taste.&quot;</li><li><a href="https://userstyles.org/styles/173240/hide-beeminder-dash-graphs" target="_blank" rel="noopener noreferrer"><strong>Hide Beeminder Dash Graphs</strong></a> - User style that hides the graph thumbnails on the dashboard. Made by @narthur.</li><li><a href="https://www.reddit.com/r/userstyles/" target="_blank" rel="noopener noreferrer"><strong>r/userstyles</strong></a> - Reddit community around the creation and use of user styles.</li><li><a href="https://stylebase.cc/" target="_blank" rel="noopener noreferrer"><strong>StyleBase</strong></a> - &quot;Collectino of UserCSS Styles&quot;</li><li><a href="https://userstyles.org/" target="_blank" rel="noopener noreferrer"><strong>Stylish</strong></a> - This website <a href="https://www.reddit.com/r/userstyles/comments/hcndfo/userstylesorg_is_ridiculously_slow_often_timing/" target="_blank" rel="noopener noreferrer">can be very slow</a>.</li><li><a href="https://github.com/openstyles/stylus" target="_blank" rel="noopener noreferrer"><strong>Stylus</strong></a> - &quot;Stylus is a fork of Stylish for Chrome, also compatible with Firefox as a WebExtension&quot;</li></ul><h2 id="hardware" tabindex="-1">Hardware <a class="header-anchor" href="#hardware" aria-hidden="true">#</a></h2><ul><li><a href="https://github.com/narthur" target="_blank" rel="noopener noreferrer">narthur</a>/<a href="https://github.com/narthur/beeminder-traffic-light" target="_blank" rel="noopener noreferrer"><strong>beeminder-traffic-light</strong></a> - Tutorial. &quot;How to use a Raspberry Pi as a Beeminder traffic light&quot;</li></ul><h2 id="scripting" tabindex="-1">Scripting <a class="header-anchor" href="#scripting" aria-hidden="true">#</a></h2><ul><li><a href="http://api.beeminder.com/#beeminder-api-reference" target="_blank" rel="noopener noreferrer"><strong>Beeminder API</strong></a> - Beeminder&#39;s API allows you to integrate Beeminder with your own scripts and programs.</li><li><a href="https://forum.beeminder.com/t/how-to-post-to-a-beeminder-goal-with-a-google-form/7746" target="_blank" rel="noopener noreferrer"><strong>How to post to a Beeminder goal with a Google Form</strong></a> - Tutorial.</li></ul><p>Note that a project&#39;s inclusion below does not mean it is official, tested, or endorsed. Use at your own risk.</p><h3 id="api-wrappers" tabindex="-1">API Wrappers <a class="header-anchor" href="#api-wrappers" aria-hidden="true">#</a></h3><ul><li><a href="https://github.com/beeminder/beeminder-gem" target="_blank" rel="noopener noreferrer">beeminder/beeminder-gem</a> - Ruby (@muflax)</li><li><a href="https://github.com/beeminder/beeminder-php-api" target="_blank" rel="noopener noreferrer">beeminder/beeminder-php-api</a> - PHP (@sodaware)</li><li><a href="https://github.com/beeminder/bee-perl" target="_blank" rel="noopener noreferrer">beeminder/bee-perl</a> - Perl</li><li><a href="https://github.com/beeminder/wolfminder" target="_blank" rel="noopener noreferrer">beeminder/wolfminder</a> - Mathematica (@dreev)</li><li><a href="https://github.com/beeminder-capstone/Nectar-Frontend/blob/develop/src/providers/beeminder-api.ts" target="_blank" rel="noopener noreferrer">beeminder-capstone/Nectar-Frontend</a> - Angular/Ionic (@nectar)</li><li><a href="https://github.com/malcolmocean/beeminderjs" target="_blank" rel="noopener noreferrer">malcolmocean/beeminderjs</a> - NodeJS (@malcolm)</li><li><a href="https://github.com/mattjoyce/beeminderpy" target="_blank" rel="noopener noreferrer">mattjoyce/<strong>beeminderpy</strong></a> - python wrapper for beeminder api</li><li><a href="https://github.com/mbork/beeminder.el" target="_blank" rel="noopener noreferrer">mbork/beeminder.el</a> - Emacs (@mbork)</li><li><a href="https://github.com/narthur/pyminder" target="_blank" rel="noopener noreferrer">narthur/<strong>pyminder</strong></a> - Beeminder library for Python</li><li><a href="https://github.com/bvtujo/beeminder-openapi" target="_blank" rel="noopener noreferrer">bvtujo/<strong>beeminder-openapi</strong></a> - OpenAPI spec for Beeminder API; can be used with <a href="https://rest.sh/#/" target="_blank" rel="noopener noreferrer">restish</a> or to generate clients in any supported language. (@are)</li></ul><h3 id="example-projects" tabindex="-1">Example Projects <a class="header-anchor" href="#example-projects" aria-hidden="true">#</a></h3><p>Beeminder users use the API to create all sorts of things! Below is only a small sample of the projects that have been published publicly online. <a href="https://github.com/search?q=beeminder&amp;type=" target="_blank" rel="noopener noreferrer">Search GitHub</a> to find more. If you know of a project you think should be added to the list below, <a href="https://github.com/narthur/beeminder-wiki" target="_blank" rel="noopener noreferrer">make a pull request with your addition</a>.</p><ul><li><a href="https://github.com/chipmanaged/MCM-Dashboard" target="_blank" rel="noopener noreferrer">chipmanaged/MCM-Dashboard</a>, including @mary&#39;s brilliant <a href="http://forum.beeminder.com/t/help-yourself-to-some-api-files/524" target="_blank" rel="noopener noreferrer">dashboard and autodial</a> features</li><li><a href="https://github.com/giovannicoppola/beEvernote" target="_blank" rel="noopener noreferrer">giovanni/evernote</a>, @giovanni&#39;s inbox-zero evernote integration</li><li><a href="https://github.com/beeminder/beebot" target="_blank" rel="noopener noreferrer">beeminder/beebot</a>, our Slack bot, by @apb</li><li><a href="https://github.com/beeminder/beemios" target="_blank" rel="noopener noreferrer">beeminder/beemios</a>, our iOS app, by @apb</li><li><a href="https://github.com/palfrey/beeminder-calendar" target="_blank" rel="noopener noreferrer">palfrey/beeminder-calendar</a>, eep days to calendar, by @palfrey</li><li><a href="https://github.com/palfrey/docket" target="_blank" rel="noopener noreferrer">palfrey/docket</a>, synch with Todoist, by @palfrey</li><li><a href="https://github.com/beeminder-capstone/Nectar-Frontend" target="_blank" rel="noopener noreferrer">beeminder-capstone/Nectar-Frontend</a>, Android, iOS and Windows mobile app, by @nectar (@rnj, @tdulcet and <a href="http://forum.beeminder.com/t/new-mobile-app-android-ios-windows-plus-7-new-integrations/3421" target="_blank" rel="noopener noreferrer">others</a>)</li><li><a href="https://github.com/beeminder-capstone/Nectar-Backend" target="_blank" rel="noopener noreferrer">beeminder-capstone/Nectar-Backend</a>, Web app and integration framework, by @nectar (@rnj, @tdulcet and <a href="http://forum.beeminder.com/t/new-mobile-app-android-ios-windows-plus-7-new-integrations/3421" target="_blank" rel="noopener noreferrer">others</a>), fork of <a href="https://github.com/tsubery/quantifier/" target="_blank" rel="noopener noreferrer">tsubery/quantifier</a></li><li><a href="https://github.com/beeminder-capstone/pebble" target="_blank" rel="noopener noreferrer">beeminder-capstone/pebble</a>, Watchapp for Pebble, by @nectar (@tdulcet)</li></ul><h4 id="elixir" tabindex="-1">Elixir <a class="header-anchor" href="#elixir" aria-hidden="true">#</a></h4><ul><li><a href="https://github.com/quantified-self-tools/altbee" target="_blank" rel="noopener noreferrer">quantified-self-tools/<strong>altbee</strong></a> - Altbee \u2014 the alternative Beeminder web interface</li><li><a href="https://github.com/quantified-self-tools/bagg" target="_blank" rel="noopener noreferrer">quantified-self-tools/<strong>bagg</strong></a> - Beebrain aggregation implementation in Elixir. Computes aggregated data points, <a href="https://forum.beeminder.com/t/github-projects-that-use-the-beeminder-api/7497/3?u=narthur" target="_blank" rel="noopener noreferrer">as described here</a>. Also exposed via Altbee&#39;s public API, <a href="https://forum.beeminder.com/t/beeminder-mathematica-package/7385/12" target="_blank" rel="noopener noreferrer">as described here</a>.</li><li><a href="https://github.com/Sodaware/beeminder.el" target="_blank" rel="noopener noreferrer">Sodaware/<strong>beeminder.el</strong></a> - Submit data to Beeminder from within Emacs. Also integrates with org-mode.</li></ul><h4 id="javascript" tabindex="-1">JavaScript <a class="header-anchor" href="#javascript" aria-hidden="true">#</a></h4><ul><li><a href="https://github.com/malcolmocean/beeminderjs" target="_blank" rel="noopener noreferrer">malcolmocean/<strong>beeminderjs</strong></a> - NodeJS wrapper for Beeminder API</li></ul><h4 id="lisp" tabindex="-1">Lisp <a class="header-anchor" href="#lisp" aria-hidden="true">#</a></h4><ul><li><a href="https://github.com/mbork/beeminder.el" target="_blank" rel="noopener noreferrer">mbork/<strong>beeminder.el</strong></a> - A Beeminder client for Emacs</li></ul><h4 id="perl" tabindex="-1">Perl <a class="header-anchor" href="#perl" aria-hidden="true">#</a></h4><ul><li><a href="https://github.com/pjjh/bmndr" target="_blank" rel="noopener noreferrer">pjjh/<strong>bmndr</strong></a>, @philip&#39;s fork of lydgate/bmndr</li></ul><h4 id="python" tabindex="-1">Python <a class="header-anchor" href="#python" aria-hidden="true">#</a></h4><ul><li><a href="https://github.com/davecahill/beesight" target="_blank" rel="noopener noreferrer">davecahill/<strong>beesight</strong></a> - Retrieves meditation data from <a href="http://insighttimer.com" target="_blank" rel="noopener noreferrer">insighttimer.com</a> and posts the data points to beeminder for tracking.</li><li><a href="https://github.com/ianmcb/beetime" target="_blank" rel="noopener noreferrer">ianmcb/<strong>beetime</strong></a> - Beeminder Anki add-on</li><li><a href="https://github.com/lastobelus/beeminder-anki" target="_blank" rel="noopener noreferrer">lastobelus/<strong>beeminder-anki</strong></a> - Anki 2.0 add-on that syncs review stats with Beeminder</li><li><a href="https://github.com/lydgate/bmndr" target="_blank" rel="noopener noreferrer">lydgate/<strong>bmndr</strong></a> - Simple python CLI for <a href="http://beeminder.com" target="_blank" rel="noopener noreferrer">beeminder.com</a></li></ul><h4 id="ruby" tabindex="-1">Ruby <a class="header-anchor" href="#ruby" aria-hidden="true">#</a></h4><ul><li><a href="https://github.com/tsubery/quantifier" target="_blank" rel="noopener noreferrer">tsubery/<strong>quantifier</strong></a> - A quantified-self app that tracks various fitness &amp; productivity metrics.</li></ul><h4 id="scala" tabindex="-1">Scala <a class="header-anchor" href="#scala" aria-hidden="true">#</a></h4><ul><li><a href="https://github.com/phdoerfler/beegment" target="_blank" rel="noopener noreferrer">phdoerfler/<strong>beegment</strong></a> - A little webservice to enhance the Beeminder API</li></ul>',34),i=[o];function s(l,h,d,p,b,m){return t(),r("div",null,i)}var u=e(n,[["render",s]]);export{c as __pageData,u as default};
